import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import { readDeck } from '../utils/api/index.js'


function Deck() {
    const [deck, setDeck] = useState([]);
    const {deckId} = useParams();

    useEffect(() => {
        const deckInfo = async () => {
            const response = await readDeck(deckId);
            setDeck(() => response);
        };
        deckInfo();
    }, [deckId])

    const { id, name, description, cards } = deck;
    if (!deck || !cards) {
        return (
            <div className="spinner-border text-primary" role="status">
               <span className="sr-only">Loading...</span>
            </div>
    )} else {
        return (
            <div className="col-9 mx-auto">
                <nav aria-label="breadcrumb">
                    <ol className="breadcrumb">
                        <li className="breadcrumb-item"><Link to={"/"}><i className="fa fa-home" aria-hidden="true"></i> Home</Link></li>
                        <li className="breadcrumb-item">{name}</li>
                    </ol>
                </nav>
                <div className="card mb-4">
                    <div className="card-body">
                        <div className="row px-3">
                            <h5 className="card-title">{name}</h5>
                        </div>
                        <p className="card-text">{description}</p>
                        <div className="row px-3">
                            <Link to={`/decks/${id}`} className="btn btn-secondary"><i class="fa fa-edit" aria-hidden="true"></i> Edit</Link>
                            <Link to={`/decks/${id}/study`} className="btn btn-primary ml-3"><i className="fa fa-bookmark" aria-hidden="true"></i> Study</Link>
                            <button className="btn btn-primary ml-3"><i class="fa fa-plus" aria-hidden="true"></i> Add Cards</button>
                            <button className="btn btn-danger ml-auto"><i className="fa fa-trash" aria-hidden="true"></i></button>
                        </div>
                    </div>
                </div>
                <div className="row pl-3 pb-3">
                    <h1>Cards</h1>
                </div>
                {cards.map((card, index) => 
                    <div className="row" key={index}>
                        <div className="col">
                            <div className="card">
                                <div className="row card-body">
                                    <p className="col-6 card-text">{card.front}</p>
                                    <p className="col-6 card-text">{card.back}</p>
                                </div>
                                <div className="d-flex justify-content-end p-4">
                                    <Link to={`/decks/${id}`} className="btn btn-secondary"><i className="fa fa-edit" aria-hidden="true"></i> Edit</Link>
                                    <button className="btn btn-danger ml-3"><i className="fa fa-trash" aria-hidden="true"></i></button>
                                </div>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        )}
}

export default Deck;

