import React from 'react';
import DeckList from "./DeckList";


function Home({ decks, getCurrentDeck }) {

    return (
        <div className="row w-75 mx-auto flex-column">
            <button type="button" className="btn btn-secondary w-25 mb-3"><i className="fa fa-plus" aria-hidden="true"></i> Create Deck</button>
            {decks.map((deck) => <DeckList key={deck.id} deck={deck} getCurrentDeck={getCurrentDeck} />)}
        </div>

    )
}

export default Home;
