import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import { readDeck, listCards } from '../utils/api/index.js'


function Study() {
    const [deck, setDeck] = useState();
    const [cards, setCards] = useState();
    const {deckId} = useParams();

    useEffect(() => { 
        const findDeck = async () => { 
            const currDeck = await readDeck(deckId);
            setDeck(()=> currDeck);    
        } 
        findDeck();
        const findCards = async () => { 
            const currCards = await listCards(deckId);
            setCards(()=> currCards);     
        } 
        findCards();

    }, [deckId]);

    if (!deck) {
        return (
            <div class="spinner-border text-primary" role="status">
                <span class="sr-only">Loading...</span>
            </div>
        )
    } else {
        return (
            <div className="col">
                <nav aria-label="breadcrumb">
                    <ol class="breadcrumb">
                        <li class="breadcrumb-item"><Link to={"/"}><i class="fa fa-home" aria-hidden="true"></i> Home</Link></li>
                        <li class="breadcrumb-item"><Link href="#"></Link>{deck.name}</li>
                        <li class="breadcrumb-item active" aria-current="page">Study</li>
                    </ol>
                </nav>
                <div className="row">Card 1</div>
            </div>
        )
    }
}

export default Study;

// The Study screen has the following features:

    // The path to this screen should include the deckId (i.e., /decks/:deckId/study).
    // There is a breadcrumb navigation bar with links to home /, followed by the name of the deck being studied and finally the text Study (e.g., Home/Rendering In React/Study).
    // The deck title (i.e., "Study: Rendering in React" ) is shown on the screen.
// Cards are shown one at a time, front-side first.
// A button at the bottom of each card "flips" it to the other side.
// After flipping the card, the screen shows a next button (see the "Next button" section below) to continue to the next card.
// After the final card in the deck has been shown, a message (see the "Restart prompt" section below) is shown offering the user the opportunity to restart the deck.
// If the user does not restart the deck, they should return to the home screen.
// Studying a deck with two or fewer cards should display a "Not enough cards" message (see the "Not enough cards" section below) and a button to add cards to the deck.
