import React, { useState, useEffect } from "react";
import Header from "./Header";
import NotFound from "./NotFound";
import DeckList from "./DeckList";
import Study from "./Study";
import { Switch, Route, Link } from "react-router-dom";
import { listDecks } from '../utils/api/index.js'
import CreateDeck from "./CreateDeck";
import Deck from "./Deck";


function Layout() {
  const [decks, setDecks] = useState([]);

  useEffect(() => { 
    const getDecks = async () => { 
      const decksFromApi = await listDecks();
        setDecks(() => decksFromApi) 
        } 
    getDecks(); 
  }, [])

  if (!decks) {
    return (
        <div className="spinner-border text-primary" role="status">
           <span className="sr-only">Loading...</span>
        </div>
    )
  } else {
    return (
      <div>
        <Header />
        <div className="container">
          {/* TODO: Implement the screen starting here */}
          <Switch>
            <Route path="/" exact>
              <div className="row w-75 mx-auto flex-column">
                <Link to="/decks/new" className="btn btn-secondary w-25 mb-3"><i className="fa fa-plus" aria-hidden="true"></i> Create Deck</Link>
                {decks.map((deck) => <DeckList key={deck.id} deck={deck} />)}
              </div>
            </Route>
            <Route path="/decks/new">
              <CreateDeck />
            </Route>
            <Route path="/decks/:deckId">
              <Deck />
            </Route>
            <Route path="/decks/:deckId/study" exact>
              <Study />
            </Route>
            <Route>
              <NotFound />  
            </Route>
          </Switch>
        </div>
      </div>
    )}
}

export default Layout;
