import React, { useState, useEffect } from "react";
import Header from "./Header";
import NotFound from "./NotFound";
import DeckList from "./DeckList";
import Study from "./Study";
import { Switch, Route, Link } from "react-router-dom";
import { listDecks } from '../utils/api/index.js'
import CreateDeck from "./CreateDeck";
import Deck from "./Deck";
import EditDeck from "./EditDeck";
import AddCard from "./AddCard";



function Layout() {
  const [deckLength, setDeckLength] = useState(0);
  const [decks, setDecks] = useState([]);

  useEffect(() => { 
    const getDecks = async () => { 
      const decksFromApi = await listDecks();
        setDecks(() => decksFromApi)
        } 
    getDecks(); 
  }, [deckLength])

  const updateDecks = (newDecks) => {
    setDeckLength(() => deckLength + newDecks)
  }

  if (!decks) {
    return (
        <div className="spinner-border text-primary" role="status">
           <span className="sr-only">Loading...</span>
        </div>
    )
  } else {
    return (
      <div>
        <Header />
        <div className="container">
          {/* TODO: Implement the screen starting here */}
          <Switch>
            <Route path="/" exact>
              <div className="row mx-auto w-75">
                <Link to="/decks/new" className="btn btn-secondary w-25 mb-3"><i className="fa fa-plus" aria-hidden="true"></i> Create Deck</Link>
              </div>
              <div className="row w-100 mx-auto flex-column align-items-center">
                {decks.map((deck) => <DeckList key={deck.id} deck={deck} updateDecks={updateDecks} />)}
              </div>
            </Route>
            <Route path="/decks/new">
              <CreateDeck />
            </Route>
            <Route path="/decks/:deckId/cards/new">
              <AddCard updateDecks={updateDecks} />
            </Route>
            <Route path="/decks/:deckId/study">
              <Study />
            </Route>
            <Route path="/decks/:deckId/edit">
              <EditDeck />
            </Route>
            <Route path="/decks/:deckId">
              <Deck updateDecks={updateDecks} />
            </Route>
            <Route>
              <NotFound />  
            </Route>
          </Switch>
        </div>
      </div>
    )}
}

export default Layout;
