import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import { readDeck } from '../utils/api/index.js'


function Deck() {
    const [deck, setDeck] = useState([]);
    const {deckId} = useParams();

    useEffect(() => {
        const deckInfo = async () => {
            const response = await readDeck(deckId);
            setDeck(() => response);
        };
        deckInfo();
    }, [deckId])

    const { id, name, description, cards } = deck;
    return (
        <div className="col-9 mx-auto">
            <nav aria-label="breadcrumb">
                <ol className="breadcrumb">
                    <li className="breadcrumb-item"><Link to={"/"}><i className="fa fa-home" aria-hidden="true"></i> Home</Link></li>
                    <li className="breadcrumb-item">{name}</li>
                </ol>
            </nav>
            <div className="card w-75 mb-4">
                <div className="card-body">
                    <div className="row px-3">
                        <h5 className="card-title">{name}</h5>
                    </div>
                    <p className="card-text">{description}</p>
                    <div className="row px-3">
                        <Link to={`/decks/${id}`} className="btn btn-secondary"><i class="fa fa-edit" aria-hidden="true"></i> Edit</Link>
                        <Link to={`/decks/${id}/study`} className="btn btn-primary ml-3"><i className="fa fa-bookmark" aria-hidden="true"></i> Study</Link>
                        <button className="btn btn-danger ml-auto"><i className="fa fa-trash" aria-hidden="true"></i></button>
                    </div>
                </div>
            </div>
        </div>



    )
}

export default Deck;
