import React, { useState, useEffect } from 'react';
import { listDecks } from '../utils/api/index.js'

function Home() {
    const [decks, setDecks] = useState([]);

    useEffect(() => { 
        async function getDecks() { 
            await listDecks().then(setDecks) 
            } 
        getDecks() 
    }, [])

    return (
        <div className="row w-75 mx-auto flex-column">
            <button type="button" className="btn btn-secondary w-25 mb-3"><Plus /> Create Deck</button>
            {decks.map((deck) => <DeckList deck={deck} />)}
        </div>
    )
}

export default Home;
