import React, { useState } from "react";
import { Link, useHistory } from 'react-router-dom';

function CreateDeck() {
    const [newDeck, setNewDeck] = useState({name: "", description: ""});
    const history = useHistory();

    const formChange = ({ target }) => {
        setNewDeck({[target.name]: target.value});
    }

    const formSubmit = ({ target }) => {
        target.preventDefault();
    }

    return (
        <div className="col-9 mx-auto">
            <nav aria-label="breadcrumb">
                <ol className="breadcrumb">
                    <li className="breadcrumb-item"><Link to={"/"}><i className="fa fa-home" aria-hidden="true"></i> Home</Link></li>
                    <li className="breadcrumb-item"><Link href="#"></Link>Create Deck</li>
                </ol>
            </nav>
            <form>
                <div class="form-group">
                    <label for="deckName">Name</label>
                    <input 
                    type="text" 
                    name="deckName"
                    value={newDeck.name}
                    id="deckName" 
                    class="form-control" 
                    placeholder="Deck Name" 
                    />
                </div>
                <div class="form-group">
                    <label for="deckDescription">Description</label>
                    <textarea
                    name="deckDescription" 
                    value={newDeck.description}
                    class="form-control" 
                    id="deckDescription" 
                    placeholder="Brief description of the deck."
                    />
                </div>
                <Link to="/" name="cancel" class="btn btn-secondary mr-3">Cancel</Link>
                <button type="submit" class="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}

export default CreateDeck;
