import React, { useEffect, useState } from "react";
import { Link, useParams } from 'react-router-dom';
import { updateCard, readDeck } from '../utils/api/index.js'

function EditCard({updateDecks}) {
    const [deck, setDeck] = useState([]);
    const [card, editCard] = useState({front: "", back: "", deckId: ""});
    const {deckId, cardId} = useParams();

    useEffect(() => {
        const deckInfo = async () => {
            const response = await readDeck(deckId);
            setDeck(() => response);
        };
        deckInfo();
    }, [deckId])

    const formChange = ({ target }) => {
        editCard({...card, [target.name]: target.value});
    }

    const formSubmit = async (event) => {
        event.preventDefault();
        await updateCard(card);
        history.push(`/decks/${deck.id}`);
        updateDecks(1);
    }

    return (
        <div className="col-9 mx-auto">
            <nav aria-label="breadcrumb">
                <ol className="breadcrumb">
                    <li className="breadcrumb-item"><Link to={"/"}><i className="fa fa-home" aria-hidden="true"></i> Home</Link></li>
                    <li className="breadcrumb-item"><Link to={`/decks/${deckId}`}>{deck.name}</Link></li>
                    <li className="breadcrumb-item">Edit Card {cardId}</li>
                </ol>
            </nav>
            <div className="row pl-3 pb-2">
                <h1>Edit Card</h1>
            </div>
            <form id="cardForm" onSubmit={formSubmit}>
                <div class="form-group">
                    <label for="front">Front</label>
                    <textarea  
                    name="front"
                    value={card.name}
                    onChange={formChange}
                    id="front" 
                    class="form-control" 
                    placeholder={card.name}
                    />
                </div>
                <div class="form-group">
                    <label for="back">Back</label>
                    <textarea
                    name="back" 
                    value={card.description}
                    onChange={formChange}
                    class="form-control" 
                    id="back" 
                    placeholder={card.description}
                    />
                </div>
                <Link to={`/decks/${deckId}`} name="cancel" class="btn btn-secondary mr-3">Done</Link>
                <button type="submit" class="btn btn-primary">Save</button>
            </form>
        </div>
    )
}

export default EditCard;
