import React, { useState, useEffect } from 'react';
import { Link, useHistory, useParams } from 'react-router-dom';
import { readDeck, listCards } from '../utils/api/index.js'


function Study() {
    const [deck, setDeck] = useState();
    const [cards, setCards] = useState();
    const [currentCard, setCurrentCard] = useState(0);
    const [frontSide, setFrontSide] = useState(true);
    const {deckId} = useParams();
    const history = useHistory();

    useEffect(() => { 
        const findDeck = async () => { 
            const currDeck = await readDeck(deckId);
            setDeck(()=> currDeck);    
        } 
        findDeck();
        const findCards = async () => { 
            const cardsList = await listCards(deckId);
            setCards(()=> cardsList);
        } 
        findCards();

    }, [deckId]);

    const nextHandler = () => {
        if (currentCard === (cards.length-1)) {
            window.confirm("Click OK to restart the deck, or CANCEL to return to the homepage.")
            ? setCurrentCard(() => 0) 
            : history.push("/");  
        } else {
            setCurrentCard((currentCard) => currentCard+1);
        }
    }

    const flipHandler = () => {
        setFrontSide(() => !frontSide)
    }

    if (!deck || !cards) {
        return (
            <div className="spinner-border text-primary" role="status">
               <span className="sr-only">Loading...</span>
            </div>
        )
    } else {
        return (
            <div className="col-9 mx-auto">
                {/* Breadcrumbs */}
                <nav aria-label="breadcrumb">
                    <ol className="breadcrumb">
                        <li className="breadcrumb-item"><Link to={"/"}><i className="fa fa-home" aria-hidden="true"></i> Home</Link></li>
                        <li className="breadcrumb-item"><Link href="#"></Link>{deck.name}</li>
                        <li className="breadcrumb-item active" aria-current="page">Study</li>
                    </ol>
                </nav>
                {/* Title */}
                <div>
                    <h1>{deck.name}: Study</h1>
                </div>
                {/* Card */}
                <div className="row p-3">
                    <div className="card w-75">
                        <div className="card-body">
                            <h5 className="card-title">Card {currentCard+1} of {cards.length}</h5>
                            <p className="card-text">{frontSide ? cards[currentCard].front : cards[currentCard].back}</p>
                            <button href="#" className="btn btn-secondary mr-3">Flip</button>
                            <button onClick={nextHandler} className="btn btn-primary">Next</button>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default Study;

// The Study screen has the following features:

    // The path to this screen should include the deckId (i.e., /decks/:deckId/study).
    // There is a breadcrumb navigation bar with links to home /, followed by the name of the deck being studied and finally the text Study (e.g., Home/Rendering In React/Study).
    // The deck title (i.e., "Study: Rendering in React" ) is shown on the screen.
    // Cards are shown one at a time, front-side first.
// A button at the bottom of each card "flips" it to the other side.
// After flipping the card, the screen shows a next button (see the "Next button" section below) to continue to the next card.
// After the final card in the deck has been shown, a message (see the "Restart prompt" section below) is shown offering the user the opportunity to restart the deck.
// If the user does not restart the deck, they should return to the home screen.
// Studying a deck with two or fewer cards should display a "Not enough cards" message (see the "Not enough cards" section below) and a button to add cards to the deck.
