import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import { readDeck } from '../utils/api/index.js'


function Deck() {
    const [deck, setDeck] = useState([]);
    const {deckId} = useParams();

    useEffect(() => {
        const deckInfo = async () => {
            const response = await readDeck(deckId);
            setDeck(() => response);
        };
        deckInfo();
    }, [deckId])

    const { id, name, description, cards } = deck;
    return (
        <div className="col-9 mx-auto">
            <nav aria-label="breadcrumb">
                <ol className="breadcrumb">
                    <li className="breadcrumb-item"><Link to={"/"}><i className="fa fa-home" aria-hidden="true"></i> Home</Link></li>
                    <li className="breadcrumb-item">{name}</li>
                </ol>
            </nav>
            <div className="card mb-4">
                <div className="card-body">
                    <div className="row px-3">
                        <h5 className="card-title">{name}</h5>
                    </div>
                    <p className="card-text">{description}</p>
                    <div className="row px-3">
                        <Link to={`/decks/${id}`} className="btn btn-secondary"><i class="fa fa-edit" aria-hidden="true"></i> Edit</Link>
                        <Link to={`/decks/${id}/study`} className="btn btn-primary ml-3"><i className="fa fa-bookmark" aria-hidden="true"></i> Study</Link>
                        <button className="btn btn-primary ml-3"><i class="fa fa-plus" aria-hidden="true"></i> Add Cards</button>
                        <button className="btn btn-danger ml-auto"><i className="fa fa-trash" aria-hidden="true"></i></button>
                    </div>
                </div>
            </div>
            <div class="row">
                <div class="col">
                    <div class="card">
                        <div class="card-body">
                            <p class="col-6 card-text">With supporting text below as a natural lead-in to additional content.</p>
                            <p class="col-6 card-text">With supporting text below as a natural lead-in to additional content.</p>
                        </div>
                        <div class="d-flex justify-content-end p-4">
                            <Link to={`/decks/${id}`} className="btn btn-secondary"><i class="fa fa-edit" aria-hidden="true"></i> Edit</Link>
                            <button className="btn btn-danger ml-3"><i className="fa fa-trash" aria-hidden="true"></i></button>
                        </div>
                    </div>
                </div>
            </div>
        </div>



    )
}

export default Deck;

// The Deck screen has the following features:

    // The path to this screen should include the deckId (i.e., /decks/:deckId).
    // There is a breadcrumb navigation bar with a link to home / followed by the name of the deck (e.g., Home/React Router).
    // The screen includes the deck name (e.g., "React Router") and deck description (e.g., "React Router is a collection of navigational components that compose declaratively in your application").
    // The screen includes "Edit", "Study", "Add Cards", and "Delete" buttons. Each button takes the user to a different destination, as follows:

// | Button Clicked | Destination |
// | -------------- | ---------------------------------------------------------------------------------------------- |
// | "Edit" | Edit Deck Screen |
// | "Study" | Study screen |
// | "Add Cards" | Add Card screen |
// | "Delete" | Shows a warning message before deleting the deck]( See the "Delete Card Prompt" section below) |

// Each card in the deck:

// is listed on the page under the "Cards" heading.
// shows a question and the answer to the question.
// has an “Edit” button that takes the user to the Edit Card screen when clicked.
